using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.UI;

public class LanternGame : MonoBehaviour
{
    [Header("Game Settings")]
    [Tooltip("Time player must keep brightness in green zone to win")]
    public float targetTime = 10f;
    
    [Tooltip("How fast brightness changes when holding/releasing")]
    public float brightnessChangeSpeed = 0.5f;
    
    [Tooltip("Input key to increase brightness")]
    public KeyCode inputKey = KeyCode.Space;

    [Header("UI References")]
    public SuccessBar successBar;
    public Text instructionText;
    public Text progressText;
    public GameObject lanternVisual; // Optional: 3D lantern object that glows

    [Header("Visual Feedback")]
    public Light lanternLight; // Optional: point light for glow effect
    public float minLightIntensity = 0f;
    public float maxLightIntensity = 2f;

    private float brightness = 0f;
    private bool isPlaying = false;
    private bool isHolding = false;

    void Start()
    {
        // Initialize brightness at middle
        brightness = 0.5f;
        
        if (instructionText != null)
        {
            instructionText.text = $"Hold {inputKey} to brighten the lantern\nKeep it in the green zone!";
        }
    }

    void Update()
    {
        if (!isPlaying) return;

        // Hold to increase brightness, release to decrease
        if (Input.GetKey(inputKey))
        {
            isHolding = true;
            brightness += brightnessChangeSpeed * Time.deltaTime;
        }
        else
        {
            isHolding = false;
            brightness -= brightnessChangeSpeed * Time.deltaTime;
        }

        // Clamp brightness between 0 and 1
        brightness = Mathf.Clamp01(brightness);

        // Update the success bar
        if (successBar != null)
        {
            successBar.UpdateBar(brightness);

            // Update progress display
            if (progressText != null)
            {
                float timeInZone = successBar.GetTimeInZone();
                progressText.text = $"Progress: {timeInZone:F1}s / {targetTime:F1}s";
            }

            // Check win condition
            if (successBar.HasMetTimeRequirement(targetTime))
            {
                OnGameComplete();
            }
        }

        // Update visual feedback
        UpdateLanternVisuals();
    }

    public void StartGame()
    {
        isPlaying = true;
        brightness = 0.5f;
        
        if (successBar != null)
        {
            successBar.ResetTimeInZone();
            successBar.UpdateBar(brightness);
        }

        if (lanternVisual != null)
        {
            lanternVisual.SetActive(true);
        }

        Debug.Log("Lantern mini-game started!");
    }

    void UpdateLanternVisuals()
    {
        // Update lantern light intensity based on brightness
        if (lanternLight != null)
        {
            lanternLight.intensity = Mathf.Lerp(minLightIntensity, maxLightIntensity, brightness);
            
            // Change light color based on success zone
            if (successBar != null && successBar.IsInSuccessZone())
            {
                lanternLight.color = Color.Lerp(Color.yellow, Color.white, 0.5f); // Warm glow
            }
            else
            {
                lanternLight.color = Color.Lerp(Color.red, Color.yellow, brightness); // Off-color
            }
        }
    }

    void OnGameComplete()
    {
        isPlaying = false;
        Debug.Log("Lantern game completed!");

        if (instructionText != null)
        {
            instructionText.text = "Perfect harmony achieved! ‚ú®";
        }

        // Play audio
        if (AudioManager.Instance != null)
            AudioManager.Instance.PlayLanternGlow();

        // Trigger game manager event
        GameManager.OnMiniGameCompleted?.Invoke();
    }

    public void StopGame()
    {
        isPlaying = false;
        
        if (lanternVisual != null)
        {
            lanternVisual.SetActive(false);
        }
    }
}

