            indicatorRect.offsetMin = Vector2.zero;
            indicatorRect.offsetMax = Vector2.zero;
        }
    }

    // Helper method for mini-games to check success over time
    public bool HasMetTimeRequirement(float requiredTime)
    {
        return timeInSuccessZone >= requiredTime;
    }
}
