```csharp
using UnityEngine;

public class EventTester : MonoBehaviour
{
    void OnEnable()
    {
        // Subscribe to all game events
        GameManager.OnMiniGameCompleted += HandleMiniGameCompleted;
        GameManager.OnItemPlacedEvent += HandleItemPlaced;
        GameManager.OnPauseRequested += HandlePauseRequested;

        Debug.Log("[EventTester] ‚úì Subscribed to all GameManager events");
    }

    void OnDisable()
    {
        // Unsubscribe to prevent memory leaks
        GameManager.OnMiniGameCompleted -= HandleMiniGameCompleted;
        GameManager.OnItemPlacedEvent -= HandleItemPlaced;
        GameManager.OnPauseRequested -= HandlePauseRequested;

        Debug.Log("[EventTester] ‚úó Unsubscribed from all events");
    }

    void HandleMiniGameCompleted()
    {
        Debug.Log("[EventTester] üéâ EVENT RECEIVED: OnMiniGameCompleted");
    }

    void HandleItemPlaced()
    {
        Debug.Log("[EventTester] üì¶ EVENT RECEIVED: OnItemPlaced");
    }

    void HandlePauseRequested()
    {
        Debug.Log("[EventTester] ‚è∏Ô∏è EVENT RECEIVED: OnPauseRequested");
    }

    // Manual triggers for testing
    [ContextMenu("Test: Fire MiniGame Complete")]
    void TestFireMiniGameComplete()
    {
        Debug.Log("[EventTester] üî• Firing OnMiniGameCompleted event...");
        GameManager.OnMiniGameCompleted?.Invoke();
    }

    [ContextMenu("Test: Fire Item Placed")]
    void TestFireItemPlaced()
    {
        Debug.Log("[EventTester] üî• Firing OnItemPlaced event...");
        GameManager.OnItemPlacedEvent?.Invoke();
    }
}
```

