using System.Collections;
using UnityEngine;

public class EventSequenceTester : MonoBehaviour
{
    public LanternGame lanternGame;
    public float delayBetweenSteps = 1f;

    [ContextMenu("Test: Full Event Sequence")]
    public void TestFullSequence()
    {
        StartCoroutine(RunEventSequence());
    }

    IEnumerator RunEventSequence()
    {
        Debug.Log("[SequenceTester] === Starting Full Event Test ===");

        // Step 1: Start game
        Debug.Log("[SequenceTester] Step 1: Starting game...");
        lanternGame.StartGame();
