        // DON'T call DontDestroyOnLoad here - parent handles it
        Debug.Log("[GameManager] Instance created");
    }
    else
    {
        Debug.LogWarning("[GameManager] Duplicate GameManager found - destroying");
        Destroy(gameObject);
    }
}
````

