using System;
using UnityEngine;

public class LanternMiniGame : MonoBehaviour
{
    [Header("Game Settings")]
    public float harmonyZoneMin = 0.4f;
    public float harmonyZoneMax = 0.6f;
    public float goalTime = 10f;
    public float brightnessChangeSpeed = 0.3f;
    public KeyCode increaseKey = KeyCode.Space;

    [Header("References")]
    public LanternUI ui;
    public LanternVisual lanternVisual;
    public GameObject lanternPrefab;
    public Transform spawnPoint;

    // State
    private float brightness = 0.5f;
    private float harmonyTime = 0f;
    private bool isPlaying = false;
    private GameObject spawnedLantern;

    // Events
    public event Action OnGameStarted;
    public event Action<LanternResult> OnGameCompleted;

    public void StartGame()
    {
        isPlaying = true;
        brightness = 0.5f;
        harmonyTime = 0f;

        SpawnLantern();
        ui.Show();
        ui.SetupHarmonyZone(harmonyZoneMin, harmonyZoneMax);

        OnGameStarted?.Invoke();
    }

    void Update()
    {
        if (!isPlaying) return;

        // Update brightness based on input
        if (Input.GetKey(increaseKey))
            brightness += brightnessChangeSpeed * Time.deltaTime;
        else
            brightness -= brightnessChangeSpeed * Time.deltaTime;

        brightness = Mathf.Clamp01(brightness);

        // Check if in harmony zone
        bool inHarmony = IsInHarmonyZone();

        // Track time in harmony
        if (inHarmony)
            harmonyTime += Time.deltaTime;

        // Update visuals (UI gets all info from here)
        ui.UpdateDisplay(brightness, inHarmony, harmonyTime, goalTime);
        lanternVisual.SetBrightness(brightness);

        // Check win condition
        if (harmonyTime >= goalTime)
            CompleteGame();
    }

    public bool IsInHarmonyZone()
    {
        return brightness >= harmonyZoneMin && brightness <= harmonyZoneMax;
    }

    void SpawnLantern()
    {
        if (lanternPrefab != null && spawnPoint != null)
        {
            spawnedLantern = Instantiate(lanternPrefab, spawnPoint.position, spawnPoint.rotation);
            lanternVisual = spawnedLantern.GetComponent<LanternVisual>();
        }
    }

    void CompleteGame()
    {
        isPlaying = false;
        ui.ShowSuccess();

        // Create result data
        LanternResult result = new LanternResult
        {
            lanternInstance = spawnedLantern,
            finalBrightness = brightness,
            completionTime = Time.time
        };

        OnGameCompleted?.Invoke(result);
    }

    public void StopGame()
    {
        isPlaying = false;
        ui.Hide();
        if (spawnedLantern != null)
            Destroy(spawnedLantern);
    }
}

