using UnityEngine;

/// <summary>
/// Central manager for all UI panels. Ensures only one panel is active at a time.
/// </summary>
public class UIManager : MonoBehaviour
{
    public static UIManager Instance { get; private set; }

    [Header("UI Panels")]
    public PersistentUI persistentUI;
    public MenuUI menuUI;
    public LanternUI lanternUI;
    public OrigamiUI origamiUI;
    public CalligraphyUI calligraphyUI;
    public PlacementUI placementUI;
    public CompletionUI completionUI;
    public TransitionUI transitionUI;

    // Track current active panel
    private GameObject currentActivePanel;

    void Awake()
    {
        if (Instance == null)
        {
            Instance = this;
        }
        else
        {
            Destroy(gameObject);
            return;
        }

        InitializeUI();
    }

    void InitializeUI()
    {
        // Persistent UI always active
        if (persistentUI != null)
            persistentUI.gameObject.SetActive(true);

        // Hide all other UI panels
        HideAll();

        Debug.Log("[UIManager] UI initialized - all panels hidden");
    }

    // === SHOW METHODS ===

    public void ShowMainMenu()
    {
        HideAll();
        if (menuUI != null)
        {
            menuUI.ShowMainMenu();
            currentActivePanel = menuUI.gameObject;
        }
    }

    public void ShowPauseMenu()
    {
        if (menuUI != null)
        {
            menuUI.ShowPauseMenu();
            currentActivePanel = menuUI.gameObject;
        }
    }

    public void ShowLanternGame()
    {
        HideAll();
        if (lanternUI != null)
        {
            lanternUI.Show();
            currentActivePanel = lanternUI.gameObject;
        }
    }

    public void ShowOrigamiGame()
    {
        HideAll();
        if (origamiUI != null)
        {
            origamiUI.Show();
            currentActivePanel = origamiUI.gameObject;
        }
    }

    public void ShowCalligraphyGame()
    {
        HideAll();
        if (calligraphyUI != null)
        {
            calligraphyUI.Show();
            currentActivePanel = calligraphyUI.gameObject;
        }
    }

    public void ShowPlacementUI()
    {
        HideAll();
        if (placementUI != null)
        {
            placementUI.Show();
            currentActivePanel = placementUI.gameObject;
        }
    }

    public void ShowRoomComplete()
    {
        HideAll();
        if (completionUI != null)
        {
            completionUI.ShowRoomComplete();
            currentActivePanel = completionUI.gameObject;
        }
    }

    public void ShowGameComplete()
    {
        HideAll();
        if (completionUI != null)
        {
            completionUI.ShowGameComplete();
            currentActivePanel = completionUI.gameObject;
        }
    }

    // === HIDE METHODS ===

    public void HideAll()
    {
        // Don't hide persistent UI
        if (menuUI != null) menuUI.HideAll();
        if (lanternUI != null) lanternUI.Hide();
        if (origamiUI != null) origamiUI.Hide();
        if (calligraphyUI != null) calligraphyUI.Hide();
        if (placementUI != null) placementUI.Hide();
        if (completionUI != null) completionUI.Hide();

        currentActivePanel = null;

        Debug.Log("[UIManager] All panels hidden");
